import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import Chatbot from "../components/Chatbot";


const subjects = [
  { name: "Mathematics", pdf: "https://firebasestorage.googleapis.com/v0/b/learnify-a7d07.firebasestorage.app/o/8th%20Grade%2Fhehd1dd.zip?alt=media&token=b2d9b3e5-9542-470f-a8c1-77bb7efafa79", image: "data:image/jpeg;base64," },
  { name: "Science", pdf: "https://firebasestorage.googleapis.com/v0/b/learnify-a7d07.firebasestorage.app/o/8th%20Grade%2Fhemh1dd.zip?alt=media&token=141866f8-bbed-431d-85c0-caf092c9fcc1", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-WjIOETlaQUhUWNq0840km-3-GJvb2w_5XA&s" },
  { name: "Social Studies", pdf: "https://firebasestorage.googleapis.com/v0/b/learnify-a7d07.firebasestorage.app/o/8th%20Grade%2Fhesc1dd.zip?alt=media&token=c7ca69ed-2009-4664-9e67-a5fe378b8cdf", image: "data:image/jpeg;base64," },
  { name: "English", pdf: "https://firebasestorage.googleapis.com/v0/b/learnify-a7d07.firebasestorage.app/o/8th%20Grade%2Fhess3dd.zip?alt=media&token=8c9adec6-6fbf-4e37-91e7-a8fa276eee65", image: "data:image/jpeg;base64," },
  { name: "Hindi", pdf: "https://firebasestorage.googleapis.com/v0/b/learnify-a7d07.firebasestorage.app/o/8th%20Grade%2Fhhbk1dd.zip?alt=media&token=94bfd46d-1b02-442b-b451-3168e2fa7747", image: "https://img.jagranjosh.com/images/2023/February/822023/cbse-10th-hindi-exam-preparation-tips.webp" },
  { name: "Computer Science", pdf: "/pdfs/computer-science.pdf", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRfvo9eFT3l0zM2PWwtjo1NFb1No8KYxN3Ktw&s" },
];

const ResourcePage = () => {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState("");

  const fetchBooks = async (searchQuery) => {
  try {
    const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchQuery}&maxResults=12`);
    const data = await response.json();
    setBooks(data.items || []);
  } catch (error) {
    console.error("Error fetching books:", error);
    setBooks([]);
  }
};

  const handleSearch = (e) => {
    e.preventDefault();
    if (query.trim()) {
      fetchBooks(query);
    }
  };

  return (
    <div className="flex">
      <Sidebar />
      <div className="p-8 w-full">
        <h1 className="text-4xl font-bold text-black mb-6 text-center">Book Resources</h1>

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="flex items-center max-w-2xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden mb-8">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search for books..."
            className="w-full px-4 py-3 text-lg outline-none"
          />
          <button type="submit" className="bg-black hover:bg-gray-700 text-white px-6 py-3 font-semibold">
            Search
          </button>
        </form>

        {/* Book Section */}
        {books.length > 0 && (
          <div className="mt-10">
            <h2 className="text-3xl font-semibold text-gray-800 mb-4">Books</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {books.map((book, index) => (
                <div key={index} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-all text-center">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">{book.volumeInfo.title}</h3>
                  <p className="text-gray-700">{book.volumeInfo.authors?.join(", ")}</p>
                  <p className="text-sm text-gray-500">{book.volumeInfo.publisher}</p>
                  <p className="text-sm text-gray-500">{book.volumeInfo.publishedDate}</p>
                  {book.volumeInfo.imageLinks?.thumbnail && (
                    <img src={book.volumeInfo.imageLinks.thumbnail} alt={book.volumeInfo.title} className="mt-4 w-32 h-40 object-cover rounded-lg mx-auto" />
                  )}
                  <a
                    href={book.volumeInfo.infoLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-700 transition"
                  >
                    View Book
                  </a>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Subject PDFs Section */}
        <div className="mt-12">
          <h2 className="text-3xl font-semibold text-gray-800 mb-4">Download 8th Grade PDFs</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {subjects.map((subject, index) => (
              <div key={index} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-all flex flex-col items-center text-center">
                <img src={subject.image} alt={subject.name} className="w-32 h-32 object-cover rounded-lg mb-4" />
                <h2 className="text-lg font-semibold text-gray-900">{subject.name}</h2>
                <a
                  href={subject.pdf}
                  download
                  className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-700 transition"
                >
                  Download PDF
                </a>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Chatbot />
    </div>
  );
};

export default ResourcePage;